mapper.select:
select p.FID id , p.FNAME name , p.FSHOW `show` , p.FORDER `order` , pm.FFIELD_KEY_HJT212 `mapping.fieldKeyHjt212` , pm.FFIELD_KEY_KNT2014 `mapping.fieldKeyKnt2014` FROM T_POLLUTANT p LEFT JOIN T_POLLUTANT_MAPPING pm ON pm.FPOLLUTANT_ID = p.FID WHERE 1 = 1
AND p.FSHOW = #{show}
ORDER BY FORDER, FID
